export interface Triangle {
	/**
	 * The first kathethe of the triangle.
	 */
	kathete1: number;
	/**
	 * The second kathethe of the triangle.
	 */
	kathete2: number;
	/**
	 * The hypothenuse of the triangle.
	 */
	hypothenuse: number;
	/**
	 * The value of the alpha angle.
	 */
	alpha: number;
	/**
	 * The value of the beta angle.
	 */
	beta: number;
	/**
	 * The value of the gamma angle.
	 */
	gamma: number;
}
